import "../App.scss";
import React, { Component } from "react";
import Header from "../components/Header/Header";
import Hero from "../components/Hero/Hero";
import Category from "../components/Category/Category";
import Card from "../components/Card/Card";
import Footer from "../components/Footer/Footer";
import axios from "axios";

const API_KEY_NYT = process.env.REACT_APP_API_KEY_NYT;

class HomePage extends Component {
  state = {
    bookData: [],
    selectedBook: null,
    bookImg: {},
    results: []
  };

  componentDidMount() {
    axios
      .get(
        `https://api.nytimes.com/svc/books/v3/lists.json?api-key=${API_KEY_NYT}&list=Hardcover Fiction`
      )
      .then((data) => {
        console.log('book', data);
        this.setState({
          bookData: data.data.results,
          selectedBook: data.data.results[0],
        });
        return data.data.results;
      })
      .then((data) => {
        data.map((obj, i) => {
          axios
            .get(
              `https://www.googleapis.com/books/v1/volumes?q=isbn:${obj.isbns[0].isbn13}`
            )
            .then((data2) => {
              //console.log('img', data2)
              console.log(data2.data.items[0].volumeInfo.imageLinks.thumbnail)
              let nytApiData = obj.isbns[0].isbn13;
              let isbn;
              let googleApiData =
                data2.data.items[0].volumeInfo.industryIdentifiers.map(
                  (obj) => {
                    isbn = obj.identifier;
                  }
                );

              // if (!isbn) {
              //   return "loading";
              // }

              //if (nytApiData === isbn) {
                this.setState({
                  bookImg: {
                    ...this.state.bookImg,
                    [i]: data2.data.items[0].volumeInfo.imageLinks.thumbnail,
                  } 
                });

              //}
            });
        });
      });
  }

  render() {
    if (this.state.bookData.length === 0 || !this.state.bookImg) {
      return (
        <section>
          <p>... Loading your bookData ...</p>
        </section>
      );
    }

    return (
      <div>
        <Header />
        <Hero />
        <Category />
        <Card
          dataInitObj={this.state.selectedBook}
          dataArr={this.state.bookData}
          bookImg={this.state.bookImg}
        />
        <Footer />
      </div>
    );
  }
}

export default HomePage;
