import "../App.scss";
import React, { Component } from "react";
import Header from "../components/Header/Header";
import Hero from "../components/Hero/Hero";
import Category from "../components/Category/Category";
import Card from "../components/Card/Card";
import Footer from "../components/Footer/Footer";
import axios from "axios";

const API_KEY_NYT = process.env.REACT_APP_API_KEY_NYT;

class HomePage extends Component {
  state = {
    bookData: [],
    selectedBook: null,
    bookImg: null,
  };

  componentDidMount() {
    axios
      .get(
        `https://api.nytimes.com/svc/books/v3/lists.json?api-key=${API_KEY_NYT}&list=Hardcover Fiction`
      )
      .then((data) => {
        // console.log(data);
        this.setState({
          bookData: data.data.results,
          selectedBook: data.data.results[0],
        });
        // return data.data.results[0].isbns[0].isbn13
        data.data.results.map((dataObj) => {
          console.log(dataObj)
          // return dataObj.isbns[0].isbn13;
        });
      })
      .then((data) => {
        // console.log(data)
        axios
          .get(`https://www.googleapis.com/books/v1/volumes?q=isbn:${data}`)
          .then((data2) => {
            // console.log(data2)
            // console.log(data2.data.items[0].volumeInfo.industryIdentifiers[1].identifier)
            // return data === data2.data.items[0].volumeInfo.industryIdentifiers[1].identifier
            if (
              data ===
              data2.data.items[0].volumeInfo.industryIdentifiers[1].identifier
            ) {
              // console.log(data2.data.items[0].volumeInfo.imageLinks.thumbnail)
              this.setState({
                bookImg: data2.data.items[0].volumeInfo.imageLinks.thumbnail,
              });
              // return data2.data.items[0].volumeInfo.imageLinks.thumbnail
            }
          });
      });
  }

  //  handleClick = () => {
  //   // e.preventDefault()
  //   this.state.bookData.forEach(bookObj=> {
  //     this.state.selectedBook = bookObj
  //   })
  // }

  // componentDidUpdate() {

  // }

  render() {
    // console.log(this.state.bookData);
    // console.log(this.state.bookImg)
    if (this.state.bookData.length === 0 && !this.state.bookImg) {
      return (
        <section>
          <p>... Loading your bookData ...</p>
        </section>
      );
    }

    return (
      <div>
        <Header />
        <Hero />
        <Category />
        <Card
          dataInitObj={this.state.selectedBook}
          dataArr={this.state.bookData}
          bookImg={this.state.bookImg}
        />
        <Footer />
      </div>
    );
  }
}

export default HomePage;
