import React from "react";
import Button from "../Button/Button";
import "./Card.scss";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faChevronLeft } from "@fortawesome/free-solid-svg-icons";
import { faChevronRight } from "@fortawesome/free-solid-svg-icons";

const Card = ({ dataInitObj, dataArr, bookImg }) => {
  // console.log(dataArr)
  let bookDataObj
  dataArr.map((dataObj)=> {
    // return bookDataObj = dataObj
    console.log(dataObj)
  })

  // console.log(bookDataObj)

  let dataObj = dataInitObj.book_details[0];


  // console.log(data);
  return (
    <div className="card__main__wrapper">
      <h3 className="card__title">Best Sellers</h3>
      <div className="card__flex">
        <FontAwesomeIcon icon={faChevronLeft} className="card__chevron--left" />
        <div className="card__wrapper">
          <img
            // src="https://via.placeholder.com/90x120"
            src={bookImg}
            className="card__img"
          ></img>
          <p>{dataObj.title}</p>
          <p>{dataObj.author}</p>
          <p>{dataObj.price === "0.00"? dataObj.price = "20.00" : dataObj.price}</p>
          <Button />
        </div>
        <FontAwesomeIcon
          icon={faChevronRight}
          className="card__chevron--right"
        />
      </div>
    </div>
  );
};

export default Card;
